	<script>
		jQuery(document).ready(function($) {
			// single product page
			if (window.location.hash) {
				try {
					$(".single-product-section").hide();
					$(`${window.location.hash}`).show();

					$(".single-product-section-link").removeClass("active");
					$(`a[href='${window.location.hash}'].single-product-section-link`).addClass("active");
				} catch (error) {
					// skip
				}
			}

			$(document).on("click", ".single-product-section-link , .woocommerce-review-link", function(e) {
				try {
					let target = $(this).attr("href") ?? null;
					if (target && target.startsWith("#")) {
						$(".single-product-section").hide();
						$(`${target}`).show();

						$(".single-product-section-link").removeClass("active");
						$(`a[href='${target}'].single-product-section-link`).addClass("active");
					}
				} catch (error) {
					// skip
				}

			});
		});
	</script>

